def palindromeIndex(s):
    if (s[::-1] == s):
        return -1
    mid = len(s)//2
    diff = 0
    size = len(s)
    offset = 0
    index = 0
    for i in range(0,mid):
        ni = i + offset
        if (s[ni] != s[size - i - 1]):
            diff += 1
            offset = 1
            index = i
        if diff > 1:
            break
    temp = s[:index] + s[index + 1:]
    if diff < 2 and temp == temp[::-1]:
        return index
    offset = 0
    diff = 0
    for i in range(0,mid):
        ni = i + offset
        if (s[i] != s[size - ni - 1]):
            diff += 1
            offset = 1
            index = size - ni - 1
        if diff > 1:
            break
    temp = s[:index] + s[index + 1:]
    if diff < 2 and temp == temp[::-1]:
        return index
    return -1
    
print(palindromeIndex(''))